mapper.select:
select

from event_receive e, plan_type pt, mechanism m, duty d, mechanism me
where e.pt_id = pt.pt_id and e.report_mechanism_id = m.id and e.mechanism_id = me.id and e.d_id = d.id and e.verify_status < 3 and e.e_id = #{id,jdbcType=BIGINT}

select

from event_receive e, plan_type pt, mechanism m, duty d, mechanism me
where e.pt_id = pt.pt_id and e.report_mechanism_id = m.id and e.d_id = d.id and e.mechanism_id = me.id and e.verify_status < 3


    order by ${order} ${orderType}

limit ${page}, ${pageSize}

mapper.update:
update event_development


        report_time = #{reportTime,jdbcType=VARCHAR}


        report_content = #{reportContent,jdbcType=VARCHAR}


        reporter = #{reporter,jdbcType=VARCHAR}


where id = #{id,jdbcType=INTEGER}
