mapper.select:
select

from reserve_plan r, event_receive e, plan_type pt, plan_version pv, plan_response pr, verify_report vr
where r.e_id = e.e_id and vr.e_id = e.e_id and vr.pv_id = pv.pv_id and vr.pr_id = pr.pr_id and e.pt_id = pt.pt_id and e.e_id = #{eId,jdbcType=BIGINT}

select

from reserve_plan r, event_receive e, plan_type pt, plan_version pv, plan_response pr, verify_report vr
where r.e_id = e.e_id and vr.e_id = e.e_id and vr.pv_id = pv.pv_id and vr.pr_id = pr.pr_id and e.pt_id = pt.pt_id and r.e_id = #{id,jdbcType=BIGINT}

select
from reserve_plan r, event_receive e, plan_type pt, plan_version pv, plan_response pr, verify_eport vr where r.e_id = e.e_id and vr.e_id = e.e_id and vr.pv_id = pv.pv_id and vr.pr_id = pr.pr_id and e.pt_id = pt.pt_id
limit ${page}, ${pageSize}
